mapper.select:

SELECT count(cbno)
FROM tbl_member
WHERE cbno = #{cbno}


SELECT count(memberid)
FROM tbl_member
WHERE memberid = #{memberid}


SELECT count(phone)
FROM tbl_member
WHERE phone = #{phone}


SELECT
	count(*)
FROM 
	tbl_member
WHERE
	memberid = #{memberid}
AND
	cbno = #{cbno}
AND
	email = #{email}

mapper.update:

UPDATE 
	tbl_member
SET
	memberpw = #{memberpw}	
WHERE
	memberid = #{memberid}

